// Keywords to identify "Staff who work at events" (e.g. Recruiters, Security, Caterers, etc.)
// vs "Clients who hire staff" (e.g. Venues, Hotels, Restaurants)
const STAFF_SERVICE_KEYWORDS = [
  'recruiter', 'staffing', 'employment', 'temp agency', 'human resources', 'agency',
  'caterer', 'catering', 'photographer', 'photography', 'video', 'production',
  'planner', 'planning', 'event management', 'entertainer', 'dj', 'music',
  'security', 'guard', 'audio', 'visual', 'lighting', 'rental', 'consultant',
  'pr', 'public relations', 'marketing', 'advertising'
];

export const parseCSV = (csvString: string): Promise<Company[]> => {
  return new Promise((resolve) => {
    Papa.parse(csvString, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const rows = results.data as RawCsvRow[];
        const processed: Company[] = rows.map((row, index) => {
          
          // Extract dynamic columns
          const emails: string[] = [];
          const phones: string[] = [];
          const linkedIns: string[] = [];
          const whatsapps: string[] = [];

          Object.keys(row).forEach(key => {
            const val = row[key]?.trim();
            if (!val) return;

            if (key.startsWith('emails/')) emails.push(val);
            if (key.startsWith('linkedIns/')) linkedIns.push(val);
            if (key.startsWith('phones/')) phones.push(val);
            // Handle primary phone column if it exists and isn't duplicate
            if (key === 'phone' && !phones.includes(val)) phones.unshift(val); 
            if (key === 'whatsapps') whatsapps.push(val);
          });

          const category = row.categoryName || 'Uncategorized';
          const isStaff = STAFF_SERVICE_KEYWORDS.some(k => category.toLowerCase().includes(k));

          // Calculate Lead Score
          let score = 0;
          if (emails.length > 0) score += 40;
          if (phones.length > 0) score += 30;
          if (linkedIns.length > 0) score += 20;
          if (row.city) score += 10;
          
          return {
            id: `row-${index}`,
            name: row.title || 'Unknown Company',
            category: category,
            city: row.city || 'Unknown',
            emails,
            phones,
            linkedIns,
            whatsapps,
            website: '',
            googleMapsUrl: row.url || '',
            hasEmail: emails.length > 0,
            hasPhone: phones.length > 0,
            hasLinkedIn: linkedIns.length > 0,
            isStaff,
            leadScore: score,
            status: 'Lead'
          };
        });
        resolve(processed);
      }
    });
  });
};

export const loadDefaultData = async (): Promise<Company[]> => {
  return parseCSV(DEFAULT_CSV_DATA);
};

export const calculateMetrics = (companies: Company[]): DashboardMetrics => {
  const total = companies.length;
  if (total === 0) {
    return {
      totalCompanies: 0,
      avgLeadScore: 0,
      withEmailCount: 0,
      withPhoneCount: 0,
      completeness: 0,
      locationCount: 0,
      emailCoverage: 0,
      phoneCoverage: 0,
      linkedInCoverage: 0,
      distribution: { emailOnly: 0, phoneOnly: 0, linkedInOnly: 0, multiple: 0 },
      topCategories: [],
      topLocations: []
    };
  }

  const withEmail = companies.filter(c => c.hasEmail).length;
  const withPhone = companies.filter(c => c.hasPhone).length;
  const withLinkedIn = companies.filter(c => c.hasLinkedIn).length;

  // Distribution logic
  let emailOnly = 0;
  let phoneOnly = 0;
  let linkedInOnly = 0;
  let multiple = 0;

  companies.forEach(c => {
    const points = (c.hasEmail ? 1 : 0) + (c.hasPhone ? 1 : 0) + (c.hasLinkedIn ? 1 : 0);
    if (points > 1) multiple++;
    else if (c.hasEmail) emailOnly++;
    else if (c.hasPhone) phoneOnly++;
    else if (c.hasLinkedIn) linkedInOnly++;
  });

  // Calculate completeness score (weighted average of fields present)
  let totalScore = 0;
  let totalLeadScore = 0;
  companies.forEach(c => {
    if (c.hasEmail) totalScore += 33.3;
    if (c.hasPhone) totalScore += 33.3;
    if (c.hasLinkedIn) totalScore += 33.3;
    totalLeadScore += c.leadScore;
  });
  const completeness = Math.round(totalScore / total);
  const avgLeadScore = Math.round(totalLeadScore / total);

  // Aggregation for charts
  const categories: Record<string, number> = {};
  const locations: Record<string, number> = {};

  companies.forEach(c => {
    const cat = c.category || 'Other';
    const loc = c.city || 'Other';
    categories[cat] = (categories[cat] || 0) + 1;
    locations[loc] = (locations[loc] || 0) + 1;
  });

  const topCategories = Object.entries(categories)
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 10);

  const topLocations = Object.entries(locations)
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 9);

  return {
    totalCompanies: total,
    avgLeadScore,
    withEmailCount: withEmail,
    withPhoneCount: withPhone,
    completeness,
    locationCount: Object.keys(locations).length,
    emailCoverage: Math.round((withEmail / total) * 100),
    phoneCoverage: Math.round((withPhone / total) * 100),
    linkedInCoverage: Math.round((withLinkedIn / total) * 100),
    distribution: {
      emailOnly,
      phoneOnly,
      linkedInOnly,
      multiple
    },
    topCategories,
    topLocations
  };
};